import React, { useState } from "react";
import Step1Tech from "../FormSteps/Step1Tech";
import Step2Interests from "../FormSteps/Step2Interests";
import Step3AboutYou from "../FormSteps/Step3AboutYou";

function Form() {

    // determine which step we're in
    const [step, setStep] = useState(0);

    // determine which title we display
    const FormTitles = ["Your Tech Stack", "Your Interests", "About You"]

    // determine which form to display
    const StepDispaly = () => {
        if (step === 0) {
            return <Step1Tech/>
        } else if (step === 1) {
            return <Step2Interests/>
        } else if (step === 2) {
            return <Step3AboutYou/>
        }
    }


    return (
        <div className="form">
            <h1>Form</h1>
            <div className="progress-bar">
                <div style={{}}>
                    {/* progress bar div is white rectangle, this div is filling colour. Adjnust CSS percentages to manipulate progress bar */}
                </div>
            </div>
            <div className="form-container">
                <div className="header">
                    <h1>{FormTitles[step]}</h1>
                </div>
                <div className="body">{ StepDispaly() }</div>
                <div className="footer">
                    <button
                    disabled={step === 0}
                    onClick={() => {
                        setStep((currentPage) => currentPage - 1)
                        }}
                        >Prev</button>
                    <button 
                    disabled={step === 2}
                    onClick={() => {
                        setStep((currentPage) => currentPage + 1)
                        }}
                        >Next</button>
                </div>
            </div>
        </div>
    )
}

export default Form
