import React, { useState } from "react";
import Step1Tech from "../FormSteps/Step1Tech";

function Form() {

    // determine which step we're in
    const [step, setStep] = useState(0);

    // determine which title we display
    const FormTitles = ["Your Tech Stack", "Your Interests", "Your Side", "About You"]

    // determine which form to display
    const StepDispaly = () => {
        if (step === 0) {
            return <Step1Tech/>
        } if (step === 1) {
            return
        }
    }


    return (
        <div className="form">
            <h1>Form</h1>
            <div className="progress-bar"></div>
            <div className="form-container">
                <div className="header">
                    <h1>{FormTitles[step]}</h1>
                </div>
                <div className="body"></div>
                <div className="footer">
                    <button
                    disabled={step === 0}
                    onClick={() => {
                        setStep((currentPage) => currentPage - 1)
                        }}
                        >Prev</button>
                    <button 
                    disabled={step === 3}
                    onClick={() => {
                        setStep((currentPage) => currentPage + 1)
                        }}
                        >Next</button>
                </div>
            </div>
        </div>
    )
}

export default Form
