import React from 'react'

const ProgressBar = ({formData}) => {

    const generateError = (dataFormField) => {
        console.log("FORM DATA: ", dataFormField)
        if (dataFormField.length < 3 || !(/[a-zA-Z]/.test(dataFormField))) {
            return 'red'
        } else {
            return 'green'
        }
    }
    // green - done; red - error ; grey - todo

    return (
        <div className='progress-bar'>
            <p className='progress-point' style={{color:generateError(formData.author)}}>About You</p>
            <p className='progress-point' style={{color:generateError(formData.interests)}}>Your Interests</p>
            <p className='progress-point' style={{color:generateError(formData.skills)}}>Your Skills</p>
            <p className='progress-point' style={{color:generateError(formData.skills_to_learn)}}>Your New Skills</p>
            <p className='progress-point' style={{color:generateError(formData.area_of_programming)}}>Area of programming</p>
        </div>
    )
}

export default ProgressBar
