import { useEffect, useState } from "react";
// make sure user decides if they want to add name to the project or not

const Project = ({navigate}) => {
    const [project, setProject] = useState(() => JSON.parse(window.localStorage.getItem("project")));

    // const project = JSON.parse(window.localStorage.getItem("project")) // this is response.data

    useEffect(() => {
        const handleStorageChange = () => {
            setProject(JSON.parse(window.localStorage.getItem("project")));
        };

        window.addEventListener('storage', handleStorageChange);
        return () => {
            window.removeEventListener('storage', handleStorageChange);
        };

    }, []);

    if (!project) {
        return <p>loading ...</p>
    } 
    const { title, description, technologies, resources, author } = project.data;

    const technologiesList = Object.keys(technologies).map(key => <li key={key}>{project.data.technologies[key]}</li>)
    const resourcesList = Object.keys(project.data.resources).map(key => <li key={key}><a href={project.data.resources[key]['link']} target='_blank' rel="noreferrer">{project.data.resources[key]["title"]}</a> </li>)

    return (

        <div className="project-container">
            <h1>{project.data.title}</h1>
            <div className="description-container">
                <p>{description}</p>
            </div>

            <h3>Technologies:</h3>
            <div className="technologies-container">
                {technologies}
            </div>

            <h3>Resources:</h3>
            <div className="resources-container">
                {resources}
            </div>

            <p>by {project.author}</p>

        </div>

        )
} 

export default Project
